<template>
  <li class="playlistitem" @click="navigateToPlayList(item.id)">
    <div class="thumb">
      <img :src="item.picUrl" alt="" />
      <span class="play-count">{{ item.playCount | formatPlayCount }}</span>
    </div>
    <h6>{{ item.name }}</h6>
  </li>
</template>

<script>
export default {
  props: {
    item: {
      type: Object,
    },
  },
  filters: {
    formatPlayCount: function (value) {
      return (value / 10000).toFixed(1) + "ä¸‡";
    },
  },

  methods: {
    navigateToPlayList: function (id) {
      this.$router.push({
        path: "/playlist",
        query: {
          id,
        },
      });
    },
  },
};
</script>

